import { defineStore } from 'pinia'

export const useUserStore = defineStore({
  id: 'user',
  state: () => ({
    token: null,
    email: null,
    name: null,
    points: null,
  }),
  actions: {
    setUserData(data) {
        this.token = data.token
        this.email = data.email
        this.name = data.name
        this.points = data.points
    }
  },
})
